mapper.insert:
insert into Main_quotation(id

    , item_code


    , item_name


    , form_code


    , form_name


    , proposer


    , dept


    , create_date


    , total
)
values (#{id}

    , #{item_code}


    , #{item_name}


    , #{form_code}


    , #{form_name}


    , #{proposer}


    , #{dept}


    , #{create_date}


    , #{total}
)

mapper.select:
select m.item_code, m.item_name, s.*
from Sub_quotation s
left join Main_quotation m on s.relation_id = m.id
left join org_member on org_member.ID = m.proposer
left join org_unit on org_member.ORG_DEPARTMENT_ID = org_unit.ID
where s.enable_status = '正常'
and s.bid_state = #{bid_state}
and s.relation_id = #{relation_id}

    and s.supplier_name = #{supplier_name}


    and org_member.NAME=#{proposer}

order by s.stock_code, s.stock_name, s.norms, s.bid_state asc

mapper.update:
update Sub_quotation


        id = #{id}


        ,offer = #{offer}


        ,offer_date = #{offer_date}


        ,issuance_date = #{issuance_date}


        ,bid_state = #{bid_state}


        ,close_date=#{close_date}


where id=#{id} and supplier_name=#{supplier_name} and stock_name = #{stock_name}
and norms = #{norms} and brand = #{brand} and stock_code = #{stock_code}
and (return_msg=#{return_msg} or return_msg is null)

update Sub_quotation


        upload_buyer = #{upload_buyer}


        upload_supplier = #{upload_supplier}


where id=#{id} and supplier_name=#{supplier_name}

UPDATE sub_quotation
SET bid_state = '5',
    return_msg=#{return_msg}
WHERE id = #{id}
  and offer = #{offer}
  and offer_date = #{offer_date}

mapper.select:
SELECT
m.item_code
,m.item_name
,s.*
FROM Sub_quotation s
LEFT JOIN Main_quotation m ON s.relation_id=m.id
WHERE s.enable_status = '正常'

    AND (s.bid_state = 2 or s.bid_state=5)
    AND s.stock_name =#{stock_name}
    AND s.norms =#{norms}


    AND s.stock_name =#{stock_name}
    AND s.norms =#{norms}
    and (s.bid_state=2 or s.bid_state=3)

select top ${limit} d.* from (SELECT c.*, row_number() over (ORDER BY files_code ASC) as rownumber FROM ( select distinct m.id,m.item_code,m.item_name,p.create_date,p.files_code,org_member.name from Main_quotation m left join Sub_quotation s on s.relation_id = m.id left join org_member on org_member.ID = m.proposer left join org_unit on org_member.ORG_DEPARTMENT_ID = org_unit.ID left join Purchase_primary p on p.id = m.id where s.enable_status = '正常' and s.bid_state = #{bid_state}
and s.supplier_name = #{supplier_name}
and org_member.NAME=#{name}
) as c) as d WHERE rownumber > ((${limit} * ${page}) - ${limit}) order by d.files_code asc

select distinct m.*,p.files_code,org_member.name
from Main_quotation m
left join Sub_quotation s on s.relation_id = m.id
left join org_member on org_member.ID = m.proposer
left join org_unit on org_member.ORG_DEPARTMENT_ID = org_unit.ID
left join Purchase_primary p on p.id = m.id
where s.enable_status = '正常' and s.bid_state = #{bid_state}

    and s.supplier_name = #{supplier_name}


    and org_member.NAME=#{name}
