mapper.select:
select c.field0002,c.sum,s.* from Purchase_sublist s left join Purchase_primary p on s.relation_id = p.id left join ( SELECT s.sublist_id,field0002 = (stuff((SELECT ',' + field0002 FROM Tabulation_listing WHERE sublist_id = s.sublist_id FOR xml path ('')),1,1,'')),COUNT(s.field0002) sum FROM Tabulation_listing s GROUP BY s.sublist_id)c on s.id=c.sublist_id where p.id = #{id} and s.enable_status='正常'
and s.brand = #{brand}
and s.stock_name=#{stock_name}
and s.norms = #{norms}
order by s.stock_code asc

select *
from Tabulation_listing
where 1=1

    and sublist_id = #{sublist_id}


    and field0001 = #{field0001}

mapper.update:
update Purchase_primary
set post_status=#{post_status}
where id = #{id}

update Purchase_sublist
set post_status=#{post_status}
where id = #{id}

update Tabulation_listing


        field0002 = #{field0002}


        ,open_date= #{open_date}


        ,close_date= #{close_date}


where sublist_id=#{sublist_id} and field0002=#{field0002}

UPDATE Purchase_primary
SET post_status = 2
WHERE id = #{id}

mapper.delete:
delete
from Tabulation_listing
where sublist_id = #{sublist_id}
  and field0002 = #{field0002}
  and field0001 = #{field0001}

